import React from "react";
import ItemList from "../comps/ItemList";
import styled from "styled-components";
import TextInput from "../comps/TextInput"

const RightWrapper = styled.div`
  display: inline-block;
  box-sizing: border-box;
  width: 50%;
  vertical-align: top;
  padding: 0px 2rem;
  @media screen and (max-width: 850px){
    display: block;
    width: 100%;
  }
`;

const ItemSubmitForm = styled.form`
  box-sizing: border-box;
  display: block;
  flex-wrap: wrap;
  position: relative;
  align-items: flex-start;
  align-content: flex-start;
  width: 100%;
  margin-bottom: 1rem;
  padding: 1rem;
  background-color: #f9ebef;
`;

const FormTitle = styled.div`
  font-size: 1rem;
  text-transform: uppercase;
  font-weight: 600;
  margin-bottom: 2rem;
`;

const ItemSubmitButton = styled.button`
  padding: 1rem 2rem;
  background: #b9607a;
  border: 1px solid #53443b;
  color: white;
  text-transform: uppercase;
  font-family: "Montserrat", Helvetica, sans-serif;
  font-size: 18px;
`;

const ButtonContainer = styled.div`
  display: block;
  position: relative;
  ::after {
    width: 100%;
    height: 100%;
    background-color: #53443b;
    border: 1px solid #53443b;
    position: absolute;
    z-index: -1;
    top: 20px;
  }
`;

const RightSide = props => {

  if (props.itemIsSelected) {
    return (
      <RightWrapper>
        <ItemSubmitForm>
          <FormTitle>What you're sharing</FormTitle>
          <TextInput changeItemInput={props.changeItemInput}></TextInput>
          <ButtonContainer><ItemSubmitButton>Share item</ItemSubmitButton></ButtonContainer>
        </ItemSubmitForm>
      </RightWrapper>
    );
  } else {
    return (
      <RightWrapper>
        <ItemList items={props.items} selectItem={props.selectItem} />
      </RightWrapper>
    );
  }
};

export default RightSide;
