import React from "react";
import styled from "styled-components";

const Table = styled.img``;

const Illustration = styled.div`
  width: calc(50% - 1rem);
  margin-right: 1rem;
  h1 {
    font-weight: 600;
    font-size: 50px;
    display: block;
    position: relative;
    padding: 1rem;
  }
`;

const LeftSide = props => {

  if(props.itemIsSelected){
    return (
      <Illustration>
        Wow you selected an item, WOOP-DEE-FREAKING-DOO!!!
      </Illustration>
    )
  }
  else{
    return (
      <Illustration>
        <h1>
          Take an item.
          <br /> Share an item.
        </h1>
        <input type='search' placeholder='search items' onChange={e => props.searchItems(e.target.value)}></input>
        <Table src="../static/table.svg"></Table>
      </Illustration>
    );
  }
};

export default LeftSide;
