import App, { Container } from "next/app";
import Head from "next/head";
import Page from "../comps/Page";
import "../static/fonts.css";

class MyApp extends App {
  constructor() {
    super();

    this.state = {
      items: [],
      searchField: "",
      itemIsSelected: false,
      itemSelected: {},
      itemInput: {
        author: "",
        userId: 0,
        id: 0,
        title: "",
        link: "",
        type: "",
        note: ""
      }
    };
  }

  componentDidMount() {
    fetch("https://my-json-server.typicode.com/andrewuebe/test-db/posts")
      .then(response => response.json())
      .then(posts => this.setState({ items: posts }));
  }

  searchItems = inputStr => {
    this.setState({ searchField: inputStr });
  };

  changeItemInput = (input, inputType) => {
    var itemInput = {...this.state.itemInput}
    switch (inputType){
      case author:
        itemInput.author = input;
        this.setState({itemInput})
        break;
      case userId:
        itemInput.userId = input;
        this.setState({itemInput})
        break;
      case title:
        itemInput.title = input;
        this.setState({itemInput})
        break;
      case link:
        itemInput.link = input;
        this.setState({itemInput})
        break;
      case type:
        itemInput.type = input;
        this.setState({itemInput})
        break;
      case note:
        itemInput.note = input;
        this.setState({itemInput})
        break;
    }
  };

  selectItem = inputID => {
    var selectedItem = this.state.items.filter(item => {
      return item.id === inputID
    });
    this.setState({ itemSelected: selectedItem });
    this.setState({ itemIsSelected: true});
  };

  render() {
    const { Component } = this.props;
    const { items, searchField } = this.state;
    const filteredItems = items.filter(item =>
      item.title.toLowerCase().includes(searchField.toLowerCase())
    );
    return (
      <Container>
        <Head>
          <title>Little Digital Library</title>
          <meta
            name="viewport"
            content="initial-scale=1.0, width=device-width"
          />
        </Head>
        <Page>
          <Component
            items={filteredItems}
            searchItems={this.searchItems}
            itemIsSelected={this.state.itemIsSelected}
            selectItem={this.selectItem}
            itemSelected={this.state.itemSelected}
            changeItemInput={this.changeItemInput}
          />
        </Page>
      </Container>
    );
  }
}

export default MyApp;
