import React from "react";
import ItemList from "../comps/item-list";
import styled from "styled-components";
import myTable from '../static/table.svg'

const Main = styled.div`
  display: flex;
`;

const Illustration = styled.div`
  width: calc(50% - 1rem);
  margin-right: 1rem;
`;

const Table = styled.img`

`;

export default class Home extends React.Component {
  constructor() {
    super();

    this.state = {
      items: [],
      users: []
    };
  }

  componentDidMount() {
    fetch("https://my-json-server.typicode.com/andrewuebe/test-db/posts")
      .then(response => response.json())
      .then(posts => this.setState({ items: posts }));
    fetch("https://my-json-server.typicode.com/andrewuebe/test-db/users")
      .then(response => response.json())
      .then(theUsers => this.setState({ users: theUsers }));
  }

  render() {
    return (
      <Main>
        <Illustration>
          <h1>Take an item.<br/> Share an item.</h1>
          <Table src="../static/table.svg"></Table>
        </Illustration>
        <ItemList items={this.state.items} />
      </Main>
    );
  }
}
