import React from "react";
import styled from "styled-components";

const Table = styled.img``;

const LeftWrapper = styled.div`
  display: inline-block;
  box-sizing: border-box;
  width: 50%;
  padding: 0px 2rem;
  vertical-align: top;
  h1 {
    font-weight: 600;
    font-size: 50px;
    display: block;
    position: relative;
  }
`;

const SearchBar = styled.input`
  padding: 120px 8px !important;
`;

const ReviewCard = styled.div`
  box-sizing: border-box;
  display: block;
  flex-wrap: wrap;
  position: relative;
  align-items: flex-start;
  align-content: flex-start;
  width: 100%;
  margin-bottom: 1rem;
  padding: 1rem;
  border: 1px solid #53443b;
  background-color: #f9ebef;
  ::after{
    content: "";
    width: 100%;
    height: 100%;
    background-color: #53443b;
    border: 1px solid #53443b;
    top: 2px;
    left: 2px;

    /* create a new stacking context */
    position: absolute;
    z-index: -1;
  }
`;

const FormTitle = styled.div`
  font-size: 2rem;
  text-transform: uppercase;
`;

const ItemInfoField = styled.div`
  display: bloxk;
  position: relative;
  padding: 32px 0px;
  font-family: "Montserrat", Helvetica, sans-serif;
  font-size: 40px;
  line-height: 48px;
  font-weight: 300;
  .typeName {
    font-size: 100px;
  }
  span {
    font-family: "Hind", Helvetica, sans-serif;
    display: block;
    position: absolute;
    font-size: 16px;
    text-transform: uppercase;
    color: #b9607a;
    top: 0px;
  }
`;

const LeftSide = props => {
  if (props.itemIsSelected) {
    return (
      <LeftWrapper>
        <ReviewCard>
          <FormTitle>You're taking:</FormTitle>
          <ItemInfoField className="typeName">
            <span>Type</span>
            {props.selectedItem[0].type.charAt(0).toUpperCase() +
              props.selectedItem[0].type.slice(1)}
          </ItemInfoField>
          <ItemInfoField>
            <span>Item Name</span>
            {props.selectedItem[0].title}
          </ItemInfoField>
        </ReviewCard>
      </LeftWrapper>
    );
  } else {
    return (
      <LeftWrapper>
        <h1>
          Take an item.
          <br /> Share an item.
        </h1>
        <SearchBar
          type="search"
          placeholder="search items"
          onChange={e => props.searchItems(e.target.value)}
        ></SearchBar>
        <Table src="../static/table.svg"></Table>
      </LeftWrapper>
    );
  }
};

export default LeftSide;
