import React, { Component } from "react";
import styled from "styled-components";

const InputFieldArea = styled.div`
    width: 100%;
`;

const InputField = styled.input`
  width: 100%;
  height: 56px;
  position: relative;
  transition: 0.3s all;
  background: none;
  box-shadow: none;
  border: none;
  border-bottom: 3px solid #afa49c;
  font-family: "Montserrat", Helvetica, sans-serif;
  color: #53443b;
  font-weight: 600;
  font-size: 50px;
  ::placeholder {
      color: #afa49c;
  }
  :hover {
      border-bottom: 3px solid #53443b;
  }
  :focus {
    outline: none;
  }
  :hover::placeholder {
      outline: none;
  }
  :focus::placeholder {
      outline: none;
      border-bottom: #53443b;
  }
}
`;

export default class TextInput extends Component {
  render() {
    return (
        <div>
            <InputField placeholder="Item name"></InputField>
            <InputFieldArea>
                <InputField></InputField>
            </InputFieldArea>
        </div>
    );
  }
}
