import React from "react";
import styled from "styled-components";

const Table = styled.img``;

const LeftWrapper = styled.div`
  display: inline-block;
  box-sizing: border-box;
  width: 50%;
  padding: 0px 2rem;
  vertical-align: top;
  h1 {
    font-weight: 600;
    font-size: 50px;
    display: block;
    position: relative;
  }
`;

const SearchBar = styled.input`
  padding: 120px 8px !important;
`;

const ItemInfoField = styled.div`
display: bloxk;
position: relative;
  padding: 32px 0px;
  font-family: "Montserrat", Helvetica, sans-serif;
  font-size: 40px;
  line-height: 48px;
  font-weight: 300;
  span{
    display: block;
    position: absolute;
    font-size: 16px;
    text-transform: uppercase;
    color: red;
    top: 0px;
  }
`;

const LeftSide = props => {

  if (props.itemIsSelected) {
    return (
    <LeftWrapper>
      <ItemInfoField>
        <span>Item Name</span>
        {props.selectedItem[0].title}
      </ItemInfoField>
    </LeftWrapper>
    )
  } else {
    return (
      <LeftWrapper>
        <h1>
          Take an item.
          <br /> Share an item.
        </h1>
        <SearchBar
          type="search"
          placeholder="search items"
          onChange={e => props.searchItems(e.target.value)}
        ></SearchBar>
        <Table src="../static/table.svg"></Table>
      </LeftWrapper>
    );
  }
};

export default LeftSide;
