import React, { Component } from "react";
import styled from "styled-components";

const InputFieldArea = styled.div`
    width: 100%;
    padding-top: 32px;
`;

const InputField = styled.input`
  width: 100%;
  height: 48px;
  position: relative;
  transition: 0.3s all;
  background: none;
  box-shadow: none;
  border: none;
  border-bottom: 3px solid #afa49c;
  font-family: "Montserrat", Helvetica, sans-serif;
  color: #53443b;
  font-size: 40px;
  font-weight: 300;
  ::placeholder {
      color: #afa49c;
  }
  :hover {
      border-bottom: 3px solid #53443b;
  }
  :focus {
    outline: none;
  }
  :hover::placeholder {
      outline: none;
  }
  :focus::placeholder {
      outline: none;
      border-bottom: #53443b;
  }
}
`;

const SelectField = styled.select`
    display: block;
    width: 100%;
    height: 48px;
    padding: 0px;
    border-radius: 0px;
    background: none;
`;


export default class TextInput extends Component {
  render() {
    return (
        <div>
            <InputField type="text" className="input-title" placeholder="Item name"></InputField>
            <InputFieldArea>
                <SelectField>
                    <option>
                        Select Type
                    </option>
                    <option>
                        Website
                    </option>
                    <option>
                        Book
                    </option>
                    <option>
                        Video
                    </option>
                    <option>
                        Movie
                    </option>
                    <option>
                        Other
                    </option>
                </SelectField>
            </InputFieldArea>
        </div>
    );
  }
}
