import React from "react";
import styled from "styled-components";

const ItemCard = styled.div`
  display: flex;
  flex-wrap: wrap;
  position: relative;
  align-items: flex-start;
  align-content: flex-start;
  width: calc(33% - 4rem);
  margin-right: 2rem;
  margin-bottom: 2rem;
  padding: 1rem;
  border: 1px solid #53443b;
  :nth-of-type(3n) {
    margin-right: 0px;
  }
`;

const ItemType = styled.div`
  font-weight: 300;
  width: 100%;
  text-transform: uppercase;
  color: #afa49c;
  margin-bottom: 0.5rem;
`;

const Item = props => {
  console.log(props);
  return (
    <ItemCard>
      <ItemType>{props.type}</ItemType>
      <h1>{props.title}</h1>
      <p>{props.note}</p>
    </ItemCard>
  );
};

export default Item;
