import React from "react";
import Item from "./Item";
import styled from "styled-components";

const Items = styled.div`
  display: inline-block;
  width: 50%;
  float: right;
`;

const ItemList = props => {
  return (
    <Items>
      {props.items.map((item, index) => (
        <Item
          key={item.id}
          title={item.title}
          author={item.author}
          type={item.type}
          note={item.note}
        ></Item>
      ))}
    </Items>
  );
};

export default ItemList;
